content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_role: true,
            muser_detail: true,
          },
        })
        .then((e) => {
          var status = [
            { value: "3", label: "TODO", color: "#4ADE80" },
            { value: "2", label: "IN PROGRESS", color: "#888DF1" },
            { value: "6", label: "SUBMITTED", color: "#2D34E6" },
          ];
          if (
            e.muser_role.name.toLowerCase() == "project manager" ||
            e.muser_role.name.toLowerCase() == "admin"
          ) {
            status.push({ value: "4", label: "REVIEW", color: "#4ADE80" });
            status.push({ value: "5", label: "REVISION", color: "#4ADE80" });
            status.push({ value: "1", label: "COMPLETE", color: "#F1B459" });
          }
          reply.send({
            status: true,
            data_act: status,
          });
        })
        .catch((e) => {
          reply.send({
            status: false,
            msg: e,
          });
        });
    } else {
      reply.send({
        status: false,
        msg: "invalid session id",
      });
    }
  } else {
    reply.send({
      status: false,
      msg: "session not found",
    });
  }
}
