content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_role: {
              select: {
                name: true,
              },
            },
          },
        })
        .then((e) => {
          // Validasi Role
          if (e.muser_role.name.toLowerCase() == "admin") {
            // Validasi Password
            let password = req.body.data.password.split("");
            if (password.length > 7) {
              // Generate password
              let in_password = ext.CryptoJS.AES.encrypt(
                req.body.data.password,
                privateKey
              ).toString();
              // Prepare main data
              let dataPrimary = {
                email: req.body.data.email,
                password: in_password,
                iduserrole: parseInt(req.body.data.userrole),
              };
              // Insert main data to muser
              db.muser
                .create({ data: dataPrimary })
                .then((e) => {
                  // Declared detail data
                  let dataDetail = {
                    iduser: e.id,
                    nik: req.body.data.nik,
                    fullname: req.body.data.fullname,
                    birthplace: req.body.data.birthplace,
                    birthdate: new Date(req.body.data.birthdate),
                    phone: req.body.data.phone,
                    address: req.body.data.address,
                    iddivision: parseInt(req.body.data.iddivision),
                  };
                  // insert detail data
                  db.muser_detail
                    .create({ data: dataDetail })
                    .then((e2) => {
                      reply.send({
                        status: true,
                        data_act: e2,
                      });
                    })
                    .catch((e2) => {
                      reply.send({
                        status: false,
                        msg: e2,
                        data: req.body.data,
                      });
                    });
                })
                .catch((e) => {
                  reply.send({
                    status: false,
                    msg: e.message,
                    data: req.body.data,
                  });
                });
            } else {
              reply.send({
                status: false,
                msg:
                  "password have at least 8 character, your password is " +
                  req.body.data.password +
                  "," +
                  password.length +
                  " character",
              });
            }
          } else {
            reply.send({
              status: false,
              msg: "you dont have access the featured",
            });
          }
        })
        .catch((e) => {
          reply.send({
            status: false,
            msg: e,
          });
        });
    } else {
      reply.send({
        status: false,
        msg: "invalid session id",
      });
    }
  } else {
    reply.send({
      status: false,
      msg: "testing",
    });
  }
}
