content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let pickDate =
    new String(req.body.data.year) + "-" + new String(req.body.data.month);
  const query = db.$queryRaw(
    "select * from v_timeline_project_detail_parent where idproject = " +
      parseInt(req.body.data.idproject) +
      " and NOT (to_char(startdate, 'YYYY-MM') > '" +
      new String(req.body.data.month) +
      "' OR to_char(duedate, 'YYYY-MM') < '" +
      new String(req.body.data.month) +
      "')  order by id"
  );
  query.then((e1) => {
    reply.send({
      status: true,
      data_act: {
        timeline: e1,
      },
    });
  })
}
