content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_role: {
              select: {
                name: true,
              },
            },
          },
        })
        .then((e) => {
          // Validasi Role
          if (e.muser_role.name.toLowerCase() == "admin") {
            // Insert department
            db.mdivision
              .update({
                where: {
                  id: parseInt(req.body.data.id),
                },
                data: {
                  name: req.body.data.name,
                  desc: req.body.data.desc,
                  iddepartment: parseInt(req.body.data.department),
                },
              })
              .then((e) => {
                reply.send({
                  status: true,
                  data_act: e,
                });
              })
              .catch((e) => {
                reply.send({
                  status: false,
                  msg: e,
                });
              });
          } else {
            reply.send({
              status: false,
              msg: "you dont have access the featured",
            });
          }
        })
        .catch((e) => {
          reply.send({
            status: false,
            msg: e,
          });
        });
    } else {
      reply.send({
        status: false,
        msg: "invalid session id",
      });
    }
  } else {
    reply.send({
      status: false,
      msg: "session not found",
    });
  }
}
