content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_role: {
              select: {
                name: true,
              },
            },
            muser_detail: true,
          },
        })
        .then((e) => {
          db.mproject_upload
            .update({
              where: {
                id: parseInt(req.body.data.idfile),
              },
              data: {
                desc: req.body.data.description,
              },
            })
            .then((e) => {
              reply.send({
                status: true,
                msg: "success update",
              });
            })
            .catch((e) => {
              reply.send({
                status: false,
                msg: "failed update",
              });
            });
          // Validasi Role
        })
        .catch((e) => {
          reply.send({
            status: false,
            msg: e,
          });
        });
    } else {
      reply.send({
        status: false,
        msg: "invalid session id",
      });
    }
  } else {
    reply.send({
      status: false,
      msg: "session not found",
    });
  }
}
