content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_detail: true,
            muser_role: {
              select: {
                name: true,
              },
            },
          },
        })
        .then((e) => {
          if (e.muser_role.name.toLowerCase() == "admin") {
          }
          if (e.muser_role.name.toLowerCase() == "directors") {
          }
          if (
            e.muser_role.name.toLowerCase() == "user" ||
            e.muser_role.name.toLowerCase() == "project manager"
          ) {
            db.muser_team
              .findFirst({
                where: {
                  AND: [
                    { iduser: e.muser_detail.id },
                    { id: parseInt(req.body.data.idteam) },
                  ],
                },
              })
              .then((e0) => {
                db.muser_team
                  .findUnique({
                    where: {
                      id: parseInt(req.body.data.idteam),
                    },
                  })
                  .then((e1) => {
                    db.muser_team_role
                      .findMany({
                        where: {
                          idteam: parseInt(req.body.data.idteam),
                        },
                        include: {
                          muser_detail: true, // Return all fields
                        },
                      })
                      .then((e2) => {
                        reply.send({
                          status: true,
                          data_act: e1,
                          data_team: e2,
                        });
                      });
                  });
              })
              .catch((e) => {
                reply.send({
                  status: false,
                  pesanindo: "kamu tidak punya akses ke projek ini",
                  msg: e,
                });
              });
          }
          // Validasi Role
        })
        .catch((e) => {
          reply.send({
            status: false,
            pesanindo: "error nih atau id tidak diketahui",
            msg: e,
          });
        });
    } else {
      reply.send({
        status: false,
        msg: "invalid session id",
      });
    }
  } else {
    reply.send({
      status: false,
      msg: "session not found",
    });
  }
}
