content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    db.muser
      .findUnique({
        where: {
          id: parseInt(data.id),
        },
        select: {
          email: true,
          muser_detail: {
            select: {
              id: true,
              fullname: true,
              mdivision: {
                select: {
                  id: true,
                  name: true,
                  mdepartment: {
                    select: {
                      id: true,
                      name: true,
                    },
                  },
                },
              },
            },
          },
          muser_role: {
            select: {
              name: true,
            },
          },
        },
      })
      .then((e) => {
        const query = db.$queryRaw(
          "select c.* from mproject a, muser_has_mproject b, muser_detail c where a.iduserproject = b.id and b.iduser = c.id and a.iddepartment = " +
            e.muser_detail.mdivision.mdepartment.id +
            " group by c.id"
        );
        query.then((e1) => {
          reply.send({
            status: true,
            data_act: e1,
          });
        });
      });
  } else {
    reply.send({
      status: false,
      data_act: "sesi not found",
      session: user,
    });
  }
}
