content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  // var user = user.user;
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();

    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    try {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_role: true,
            muser_detail: {
              include: {
                mdivision: true,
                muser: true,
              },
            },
          },
        })
        .then((e) => {
          var dd = ext.CryptoJS.AES.decrypt(
            e.muser_detail.muser.password,
            privateKey
          ).toString(ext.CryptoJS.enc.Utf8);
          reply.send({
            status: true,
            data_act: e,
            passwordDecrypt: dd,
            userData: user,
          });
        });
    } catch (exception_var) {
      reply.send({
        status: false,
        msg: "invalid session, user not found",
      });
    }
  } else {
    reply.send({
      status: false,
      data_act: "sesi not found",
    });
  }
}
