content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data_split = a_data.split(";");
    let data = {
      id: data_split[0],
      email: data_split[2],
    };
    db.muser_team
      .findMany({
        where: {
          iduser: parseInt(data.id),
        },
        select: {
          id: true,
          name: true,
        },
      })
      .then((e) => {
        reply.send({
          status: true,
          data_act: e,
        });
      });
  } else {
    reply.send({
      status: false,
      data_act: "Invalid session",
    });
  }
}
