content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.id) {
    let a_data = ext.CryptoJS.AES.decrypt(req.session.id, privateKey).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data_split = a_data.split(";");
    let data = {
      id: data_split[0],
      username: data_split[1],
      email: data_split[2],
    };
    db.muser
      .findUnique({
        where: {
          id: parseInt(data.id),
        },
        select: {
          email: true,
          muser_role: {
            select: {
              name: true,
            },
          },
        },
      })
      .then((e) => {
        const data = db.muser.findMany({
          select: {
            id: true,
            email: true,
            muser_detail: true,
            muser_role: {
              select: {
                name: true,
              },
            },
          },
        });
        data.then((e) => {
          reply.send({
            status: true,
            data_act: e,
          });
        });
      });
  } else {
    reply.send({
      status: false,
      data_act: "sesi not found",
      session: user,
    });
  }
}
