content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_detail: true,
            muser_role: {
              select: {
                name: true,
              },
            },
          },
        })
        .then((e) => {
          if (e.muser_role.name.toLowerCase() == "admin") {
            reply.send({
              status: false,
              msg: "Fitur belum tersedia untuk role admin",
            });
          }
          if (e.muser_role.name.toLowerCase() == "directors") {
            reply.send({
              status: false,
              msg: "Fitur belum tersedia untuk role directors",
            });
          }
          if (
            e.muser_role.name.toLowerCase() == "user" ||
            e.muser_role.name.toLowerCase() == "project manager"
          ) {
            db.muser_has_mproject
              .findFirst({
                where: {
                  AND: [
                    { iduser: e.muser_detail.id },
                    { idproject: parseInt(req.body.data.idproject) },
                  ],
                },
              })
              .then((e0) => {
                if (e0.idproject == parseInt(req.body.data.idproject)) {
                  db.mproject_detail
                    .findMany({
                      where: {
                        idparent: parseInt(req.body.data.idparent),
                      },
                      include: {
                        mproject_status: true,
                        mpriority: true,
                      },
                    })
                    .then((e1) => {
                      reply.send({
                        status: true,
                        data_act: e1,
                      });
                    })
                    .catch((e) => {
                      reply.send({
                        status: false,
                        msg: e,
                      });
                    });
                } else {
                  reply.send({
                    status: false,
                    msg: "you dont have access with this project",
                  });
                }
              });
          }
        })
        .catch((e) => {
          reply.send({
            status: false,
            pesanindo: "error nih atau id tidak diketahui",
            msg: e,
          });
        });
    } else {
      reply.send({
        status: false,
        msg: "invalid session id",
      });
    }
  } else {
    reply.send({
      status: false,
      msg: "session not found",
    });
  }
}
