content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const query = db.$queryRaw(
    "select * from v_timeline_project_detail_parent where idproject = 56 and NOT (to_char(startdate, 'YYYY-MM') > '2021-11' OR to_char(duedate, 'YYYY-MM') < '2021-11')  order by id"
  );
  query.then((e1) => {
    reply.send({
      status: true,
      data_act: {
        timeline: e1,
      },
    });
  })
}
