content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_role: {
              select: {
                name: true,
              },
            },
          },
        })
        .then((e) => {
          if (e.muser_role.name.toLowerCase() == "admin") {
            try {
              db.muser
                .findMany({
                  skip: 0,
                  take: 10,
                  select: {
                    id: true,
                    email: true,
                    muser_detail: true,
                    muser_role: true,
                  },
                  orderBy: [
                    {
                      id: "desc",
                    },
                  ],
                })
                .then((e) => {
                  reply.send({
                    status: true,
                    data_act: e,
                  });
                });
            } catch (exception_var) {
              reply.send({
                status: false,
                msg: "required parameter, check again your input",
              });
            }
          } else {
            reply.send({
              status: false,
              msg: "Don't have access to the feature",
            });
          }
        });
    } else {
      reply.send({
        status: false,
        data_act: "token invalid ",
      });
    }
  } else {
    reply.send({
      status: false,
      data_act: "sesi not found",
    });
  }
}
