content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    db.muser
      .findUnique({
        where: {
          id: parseInt(data.id),
        },
        select: {
          email: true,
          muser_detail: true,
          muser_role: {
            select: {
              name: true,
            },
          },
        },
      })
      .then((e) => {
        const query = db.$queryRaw(
          "select a.*, b.name as namestatus, c.name as namepriority from mproject_detail a,  mproject_status b , mpriority c  where a.iddivision =  " +
            e.muser_detail.iddivision +
            " and a.idstatus = b.id and a.idstatus IN (2,3,5) and a.idparent is null and a.priority = c.id ORDER BY a.id DESC"
        );
        query.then((e1) => {
          reply.send({
            status: true,
            data_act: e1,
          });
        });
      });
  } else {
    reply.send({
      status: false,
      data_act: "sesi not found",
      session: user,
    });
  }
}
