content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    db.muser
      .findUnique({
        where: {
          id: parseInt(data.id),
        },
        select: {
          email: true,
          muser_detail: true,
          muser_role: {
            select: {
              name: true,
            },
          },
        },
      })
      .then((e) => {
        if (e.muser_role.name.toLowerCase() == "admin") {
          const query = db.$queryRaw(
            "select a.* from mproject a, muser_has_mproject b where a.iduserproject = b.id"
          );
          query.then((e) => {
            reply.send({
              status: true,
              data_act: e,
            });
          });
        }
        if (
          e.muser_role.name.toLowerCase() == "project manager" ||
          e.muser_role.name.toLowerCase() == "user"
        ) {
          const query = db.$queryRaw(
            "select a.*, c.name as status from mproject a, muser_has_mproject b, mproject_status c where a.iduserproject = b.id and a.idstatus = c.id and b.iduser =" +
              e.muser_detail.id
          );
          query.then((e1) => {
            db.$queryRaw(
              "select b.*, count(a.idstatus) as nproject from mproject a, mproject_status b,muser_has_mproject c where a.idstatus = b.id and a.id = c.idproject and c.iduser =" +
                e.muser_detail.id +
                "group by a.idstatus, b.id"
            ).then((e2) => {
              // e1.dashboard = "haloooo";
              reply.send({
                status: true,
                data_act: e1,
                dashboard: e2,
              });
            });
          });
        }
        if (e.muser_role.name.toLowerCase() == "director") {
          const query = db.$queryRaw(
            "select a.* from mproject a, muser_has_mproject b where a.iduserproject = b.id and a.iddepartment =" +
              e.muser_detail.idepartment
          );
          query.then((e) => {
            reply.send({
              status: true,
              data_act: e,
            });
          });
        }
      });
  } else {
    reply.send({
      status: false,
      data_act: "sesi not found",
      session: user,
    });
  }
}
