content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const privateKey = "Team 6 Andromedia";
  if (user.user.id) {
    let token_private = ext.CryptoJS.SHA3(privateKey, {
      outputLength: 512,
    }).toString();
    let a_data = ext.CryptoJS.AES.decrypt(user.user.id, token_private).toString(
      ext.CryptoJS.enc.Utf8
    );
    let data = JSON.parse(a_data);
    if (data) {
      db.muser
        .findUnique({
          where: {
            id: parseInt(data.id),
          },
          select: {
            email: true,
            muser_role: true,
            muser_detail: true,
          },
        })
        .then((e) => {
          // reply.send({
          //   status: false,
          //   msg: "Failed update status because id status is invalid",
          //   data: e,
          // });
          if (
            e.muser_role.name.toLowerCase() == "project manager" ||
            e.muser_role.name.toLowerCase() == "user"
          ) {
            db.mproject_detail
              .findUnique({
                where: {
                  id: parseInt(req.body.data.id),
                },
              })
              .then((e1) => {
                // reply.send({
                //   status: false,
                //   msg: "Failed update status because id status is invalid",
                //   data: e1,
                // });
                if (
                  e.muser_role.name.toLowerCase() == "project manager" ||
                  e1.iddivision == e.muser_detail.iddivision
                ) {
                  db.mproject_detail
                    .update({
                      where: {
                        id: parseInt(req.body.data.id),
                      },
                      data: {
                        idstatus: parseInt(req.body.data.idstatus),
                      },
                    })
                    .then((e) => {
                      reply.send({
                        status: true,
                        data_act: e,
                      });
                    })
                    .catch((e) => {
                      reply.send({
                        status: false,
                        msg:
                          "Failed update status because id status is invalid",
                      });
                    });
                } else {
                  reply.send({
                    status: false,
                    msg: "You dont have access with the task",
                  });
                }
              });
          } else {
            reply.send({
              status: false,
              msg: "you dont have access the featured",
            });
          }
        })
        .catch((e) => {
          reply.send({
            status: false,
            msg: e,
          });
        });
    } else {
      reply.send({
        status: false,
        msg: "invalid session id",
      });
    }
  } else {
    reply.send({
      status: false,
      msg: "session not found",
    });
  }
}
